function ExportExcel(e,c){var d=document.getElementById(e);if(d.nodeName=="TABLE"){var b=document.createElement("a");b.href="data:application/vnd.openxmlformats-officedocument.spreadsheetml.sheet,"+encodeURIComponent(d.outerHTML);b.setAttribute("download",c+"_"+new Date().toLocaleString()+".xlsx");b.click()}else{alert("Not a table")}}function cExportExcel(d,b){var c=document.getElementById(d);if(c.nodeName=="TABLE"){var a=['<div id="selection_list"><table border="1" width="100%"><thead><tr><th><input type="button" onclick="$(\'#selection_list input:checkbox\').prop(\'checked\', true);" value="Select All"/></th><th>Column Name</th></tr></thead><tbody>'];$.each(c.rows[0].cells,function(f,e){a.push("<tr>");a.push('<td align="center"><input name="selection_column" type="checkbox" value="'+f+'")/></td>');a.push('<td align="center">'+e.innerHTML.trim()+"</td>");a.push("</tr>")});a.push('</tbody><lable>File Name : </lable><input value="'+b+'" id="strFileName" type="text" placeholder="File Name"/><button onclick="cExport(\''+d+"')\">Save</button></div>");$.colorbox({html:a.join(""),width:"50%",opacity:0.5,title:"Select Columns to Save"})}else{alert("Not a table")}}function cExport(c){var b=[];$.each(document.getElementById(c).tHead.rows[0].cells,function(e,d){b.push(e)});var a=[];$("#selection_list input[name=selection_column]:checked").each(function(e,d){a.push(parseInt(d.value))});rExportExcel(c,$("#strFileName").val()||"gs_report",$(b).not(a).get());$.colorbox.remove()}function rExportExcel(f,b,e){if(document.getElementById(f).nodeName=="TABLE"){var d=$("#"+f).clone().get(0);for(var c=0;c<e.length;c++){d.tHead.rows[0].deleteCell((e[c]-c));for(var a=0;a<d.tBodies[0].rows.length;a++){d.tBodies[0].rows[a].deleteCell((e[c]-c))}}strExportExcel(d.outerHTML,b)}else{alert("Not a table")}}function strExportExcel(d,c){var b=document.createElement("a");b.href="data:application/vnd.openxmlformats-officedocument.spreadsheetml.sheet,"+encodeURIComponent(d);b.setAttribute("download",c+"_"+new Date().toLocaleString()+".xlsx");b.click()};